mapper.select:
SELECT A.RNUM , A.NUM , A.CATEGORY , A.WRITER , A.SUBJECT , A.CONTENT , A.REG_DATE , A.UPD_DATE , A.ADP_YN , A.DEL_YN , A.PARENT_NUM , A.LIKE_COUNT , A.SCRAP_COUNT , A.COUNT ,(SELECT COUNT(*) FROM QA_BOARD COM WHERE COM.PARENT_NUM = A.NUM AND COM.DEL_YN = 'N' ) AS COMM_CNT FROM (SELECT @ROWNUM:=@ROWNUM+1 AS RNUM , QB.NUM , QB.CATEGORY , QB.WRITER , TRIM(QB.SUBJECT) SUBJECT , QB.CONTENT , QB.REG_DATE , QB.UPD_DATE , QB.ADP_YN , QB.DEL_YN , QB.PARENT_NUM , QB.LIKE_COUNT , QB.SCRAP_COUNT , QB.COUNT FROM QA_BOARD QB , (SELECT @ROWNUM:=0) R WHERE 1=1 AND CATEGORY = CASE WHEN #{searchType} != 'all' THEN #{searchType} ELSE CATEGORY END AND SUBJECT LIKE CONCAT ('%', #{keyword}, '%') AND DEL_YN = 'N' AND QB.NUM > 0 AND PARENT_NUM is NULL GROUP BY QB.NUM ORDER BY NUM DESC ) AS A
ORDER BY A.NUM DESC
LIMIT #{firstRecordIndex}, #{RECORDSPERPAGE}

mapper.update:
UPDATE
qa_board
SET
subject = #{subject}
, content = #{content}
, upd_date = (SELECT date_format(now(),'%Y-%c-%d'))
WHERE num = #{num}

UPDATE qa_board
SET
ADP_YN = 'Y'
, ADP_NUM = #{num}
WHERE num = #{parentNum}

mapper.select:
SELECT A.RNUM , A.NUM , A.CATEGORY , A.WRITER , A.SUBJECT , A.CONTENT , A.REG_DATE , A.UPD_DATE , A.ADP_YN , A.DEL_YN , A.PARENT_NUM , A.LIKE_COUNT , A.SCRAP_COUNT , A.COUNT , (SELECT COUNT(*) FROM QA_BOARD COM WHERE COM.PARENT_NUM = A.NUM AND COM.DEL_YN = 'N' ) AS COMM_CNT FROM (SELECT @ROWNUM:=@ROWNUM+1 AS RNUM , QB.NUM , QB.CATEGORY , QB.WRITER , TRIM(QB.SUBJECT) SUBJECT , QB.CONTENT , QB.REG_DATE , QB.UPD_DATE , QB.ADP_YN , QB.DEL_YN , QB.PARENT_NUM , QB.LIKE_COUNT , QB.SCRAP_COUNT , QB.COUNT FROM QA_BOARD QB , (SELECT @ROWNUM:=0) R WHERE 1=1 AND CATEGORY = CASE WHEN #{searchType} != 'all' THEN #{searchType} ELSE CATEGORY END AND SUBJECT LIKE CONCAT ('%', #{keyword}, '%') AND WRITER = #{writer} AND DEL_YN = 'N' AND QB.NUM > 0 AND PARENT_NUM is NULL GROUP BY QB.NUM ORDER BY NUM DESC ) AS A
ORDER BY A.NUM DESC
LIMIT #{firstRecordIndex}, #{RECORDSPERPAGE}

SELECT A.RNUM , A.NUM , A.CATEGORY , A.WRITER , A.SUBJECT , A.CONTENT , A.REG_DATE , A.UPD_DATE , A.ADP_YN , A.DEL_YN , A.PARENT_NUM , A.LIKE_COUNT , A.SCRAP_COUNT FROM (SELECT @ROWNUM:=@ROWNUM+1 AS RNUM , QB.NUM , QB.CATEGORY , QB.WRITER , TRIM(QB.SUBJECT) SUBJECT , QB.CONTENT , QB.REG_DATE , QB.UPD_DATE , QB.ADP_YN , QB.DEL_YN , QB.PARENT_NUM , QB.LIKE_COUNT , QB.SCRAP_COUNT FROM QA_BOARD QB , (SELECT @ROWNUM:=0) R WHERE 1=1 AND CATEGORY = CASE WHEN #{searchType} != 'all' THEN #{searchType} ELSE CATEGORY END AND SUBJECT LIKE CONCAT ('%', #{keyword}, '%') AND WRITER = #{writer} AND DEL_YN = 'N' AND QB.NUM > 0 AND PARENT_NUM is NOT NULL GROUP BY QB.NUM ORDER BY NUM DESC ) AS A
ORDER BY A.NUM DESC
LIMIT #{firstRecordIndex}, #{RECORDSPERPAGE}
