mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY QA.NUM DESC) AS RNUM , QA.NUM , QA.CATEGORY , QA.WRITER , QA.SUBJECT , QA.CONTENT , QA.REG_DATE , QA.UPD_DATE , QA.DEL_YN , QA.PARENT_NUM , QA.LIKE_COUNT , QA.SCRAP_COUNT , COUNT(COM.BOARD_IDX) AS COMM_CNT FROM QA_BOARD QA LEFT JOIN COMMENT COM ON QA.NUM = COM.BOARD_IDX AND COM.DEL_YN = 'n' AND COM.BOARD_TYPE = 'tip' INNER JOIN SCRAP_BOARD SB ON QA.NUM = SB.BOARD_IDX WHERE SB.USER_ID = #{writer} AND SB.BOARD_TYPE = 'qa' AND QA.DEL_YN = 'N' AND QA.NUM > 0 AND CATEGORY = CASE WHEN #{searchType} != 'all' THEN #{searchType} ELSE CATEGORY END AND SUBJECT LIKE CONCAT ('%', #{keyword}, '%')
GROUP BY QA.NUM ORDER BY
CASE WHEN TRIM(REGEXP_REPLACE(SUBJECT, '<[^>]+>', '')) = '' THEN 1 WHEN SUBJECT REGEXP '^[가-힣ㄱ-ㅎㅏ-ㅣ]' THEN 2 WHEN SUBJECT REGEXP '^[A-Za-z]' THEN 3 WHEN SUBJECT REGEXP '^[0-9]' THEN 4 ELSE 5 END, QA.NUM DESC LIMIT #{firstRecordIndex}, #{RECORDSPERPAGE}

SELECT A.RNUM , A.NUM , A.CATEGORY , A.WRITER , A.SUBJECT , A.CONTENT , A.REG_DATE , A.UPD_DATE , A.ADP_YN , A.DEL_YN , A.PARENT_NUM , A.LIKE_COUNT , A.SCRAP_COUNT ,(SELECT COUNT(*) FROM QA_BOARD COM WHERE COM.PARENT_NUM = A.NUM AND COM.DEL_YN = 'N' ) AS COMM_CNT FROM (SELECT @ROWNUM:=@ROWNUM+1 AS RNUM , QB.NUM , QB.CATEGORY , QB.WRITER , QB.SUBJECT , QB.CONTENT , QB.REG_DATE , QB.UPD_DATE , QB.ADP_YN , QB.DEL_YN , QB.PARENT_NUM , QB.LIKE_COUNT , QB.SCRAP_COUNT FROM QA_BOARD QB , (SELECT @ROWNUM:=0) R WHERE 1=1 AND CATEGORY = CASE WHEN #{searchType} != 'all' THEN #{searchType} ELSE CATEGORY END AND SUBJECT LIKE CONCAT ('%', #{keyword}, '%') AND DEL_YN = 'N' AND QB.NUM > 0
AND PARENT_NUM is NULL
GROUP BY QB.NUM ORDER BY NUM DESC ) AS A LIMIT #{firstRecordIndex}, #{RECORDSPERPAGE}

mapper.update:
UPDATE
qa_board
SET
subject = #{subject}
, content = #{content}
, upd_date = (SELECT date_format(now(),'%Y-%c-%d'))
WHERE num = #{num}

UPDATE qa_board
SET
ADP_YN = 'Y'
, ADP_NUM = #{num}
WHERE num = #{parentNum}
