mapper.select:
SELECT A.RNUM ,A.NUM ,A.CATEGORY ,A.WRITER ,A.SUBJECT ,A.CONTENT ,A.REG_DATE ,A.UPD_DATE ,A.DEL_YN ,A.PARENT_NUM ,A.LIKE_COUNT ,A.SCRAP_COUNT ,(SELECT COUNT(*) FROM COMMENT COM WHERE COM.BOARD_IDX = A.NUM AND COM.DEL_YN = 'n' AND COM.BOARD_TYPE ='tip') AS COMM_CNT FROM (SELECT @ROWNUM:=@ROWNUM+1 AS RNUM ,TB.NUM ,TB.CATEGORY ,TB.WRITER ,TB.SUBJECT ,TB.CONTENT ,TB.REG_DATE ,TB.UPD_DATE ,TB.DEL_YN ,TB.COUNT ,TB.PARENT_NUM ,TB.LIKE_COUNT ,TB.SCRAP_COUNT FROM TIP_BOARD TB ,(SELECT @ROWNUM:=0) R WHERE 1=1 AND CATEGORY = CASE WHEN #{searchType} != 'all' THEN #{searchType} ELSE CATEGORY END AND SUBJECT LIKE CONCAT ('%', #{keyword}, '%') AND DEL_YN = 'N'
AND TB.NUM > 0
GROUP BY TB.NUM ORDER BY NUM DESC ) AS A LIMIT #{firstRecordIndex}, #{RECORDSPERPAGE}

mapper.insert:
INSERT INTO TIP_BOARD
      (NUM
      ,CATEGORY
      ,WRITER
      ,SUBJECT
      ,CONTENT
      ,REG_DATE
      ,DEL_YN
      )
VALUES
      ((SELECT NVL(MAX(num), 0) + 1 FROM tip_board ALIAS_FOR_SUBQUERY)
      ,#{category}
      ,#{writer}
      ,#{subject}
      ,#{content}
      ,(SELECT date_format(now(),'%Y-%c-%d'))
      ,'N'
      )

mapper.update:
UPDATE TIP_BOARD
   SET DEL_YN = 'Y'
 WHERE NUM = #{num}

UPDATE TIP_BOARD
   SET CATEGORY = #{category}
      ,SUBJECT = #{subject}
      ,CONTENT = #{content}
      ,UPD_DATE = (SELECT date_format(now(),'%Y-%c-%d'))
WHERE NUM = #{num}

mapper.select:
SELECT NUM
  FROM TIP_BOARD
 WHERE WRITER = #{writer}
   AND SUBJECT = #{subject}
   AND CONTENT = #{content}
   AND DEL_YN = 'N'
 ORDER BY NUM desc limit 1
